modl.$module({

    "mod-d": {
        "/module": function() {
            "use strict";

            modl.
            unit(function(module) {
                module.exports.name = "mod-d";
            });
        }
    },

    "/module": function() {
        "use strict";

        modl.
        uses("mod-d").
        unit(function(module, d) {
            module.exports.name = "mod-c";
            module.exports.d = d;
        });
    }
});