modl.$module({

    "mod-g": {
        "/module": function() {
            "use strict";

            modl.
            unit(function(module) {
                module.exports.name = "mod-g";
            });
        }
    },

    "mod-h": {
        "mod-g": "mod-g",

        "/module": function() {
            "use strict";

            modl.
            uses("mod-g").
            unit(function(module, g, f) {
                module.exports.name = "mod-h";
                module.exports.g = g;
            });
        }
    },

    "/module": function() {
        "use strict";

        modl.
        uses("mod-g").
        uses("mod-h").
        unit(function(module, g, h) {
            module.exports.name = "mod-f";
            module.exports.g = g;
            module.exports.h = h;
        });
    }
});